import Vue from 'vue';
import VueRouter from 'vue-router';

Vue.use(VueRouter);

const routes = [
  {
    path: '/login',
    name: 'Login',
    component: () => import('../views/Element/LoginView.vue')
  },
  {
    path: '/users',
    name: 'Users',
    component: () => import('../views/Element/ShowTaskView.vue'),
    meta: { requiresAuth: true } // 需要身份验证的路由
  }
];

const router = new VueRouter({
  mode: 'history', // 设置使用历史模式
  routes
});

// 添加全局导航守卫
router.beforeEach((to, from, next) => {
  const token = localStorage.getItem('access_token');
  const loggedIn = !!token; // 转换为布尔值

  console.log(`从 ${from.path} 到 ${to.path}，登录状态：${loggedIn}，token：${token}`);

  if (to.matched.some(record => record.meta.requiresAuth) && !loggedIn) {
    console.log('需要认证但未登录，重定向到 /login');
    next('/login');
  } else if (to.path === '/login' && loggedIn) {
    console.log('已登录，重定向到 /users');
    next('/users');
  } else {
    console.log('继续导航');
    next();
  }
});

export default router;
