import Vue from 'vue';
import VueRouter from 'vue-router';

Vue.use(VueRouter);

const routes = [
  {
    path: '/login',
    name: 'Login',
    component: () => import('../views/Element/LoginView.vue')
  },
  {
    path: '/users',
    name: 'Users',
    component: () => import('../views/Element/ShowTaskView.vue'),
    meta: { requiresAuth: true } // 需要身份验证的路由
  }
];

const router = new VueRouter({
  mode: 'history', // 设置使用历史模式
  routes
});

// 添加全局导航守卫
router.beforeEach((to, from, next) => {
  const loggedIn = !!localStorage.getItem('access_token'); // 转换为布尔值

  // 打印日志以调试
  console.log(`从 ${from.path} 到 ${to.path}，登录状态：${loggedIn}`);

  if (to.matched.some(record => record.meta.requiresAuth) && !loggedIn) {
    // 如果目标路由需要认证且未登录
    next('/login'); // 重定向到登录页面
  } else if ((to.path === '/login' || to.path === '/register') && loggedIn) {
    // 防止已登录用户访问登录或注册页面
    next('/users');
  } else {
    next(); // 继续导航
  }
});

export default router;
