import Vue from 'vue'
import VueRouter from 'vue-router'


Vue.use(VueRouter)

const routes = [
  {
    path: '/login',
    name: 'Login',
    component: () => import('../views/Element/LoginView.vue')
  },
  {
    path: '/register',
    name: 'Register',
    component: () => import( '../views/Element/RegisterView.vue')
  },
  {
    path: '/users',
    name: 'Users',
    component: () => import( '../views/Element/ShowTaskView.vue')
  }
];

// // 添加全局导航守卫
// router.beforeEach((to, from, next) => {
//   const loggedIn = localStorage.getItem('userToken');
//
//   if (to.matched.some(record => record.meta.requiresAuth) && !loggedIn) {
//     next('/login'); // 如果没有令牌，重定向到登录页面
//   } else {
//     next(); // 否则继续
//   }
// });

const router = new VueRouter({
  mode: 'history', // 设置使用历史模式
  routes
});


// 添加全局导航守卫
router.beforeEach((to, from, next) => {
  const loggedIn = localStorage.getItem('userToken');

  if (to.matched.some(record => record.meta.requiresAuth) && !loggedIn) {
    next('/login'); // 如果未认证，重定向到登录页面
  } else {
    next(); // 否则，继续导航
  }
});


export default router
